



//Defining a string of all the letters
var letterString="abcdefghijklmnopqrstuvwxyz";
//A variable which will be containing the random number generated by computer 
var random_number= Math.floor(Math.random() *26);
//Defining variable which contains number of wins,losses and the number of left chances 
var wins=0;
var losses=0;
var left=9; 
//This variable holds the the user guesses up to the current time of playing 
var guess_string="";
//Thus variable shows if the user guess is the first guess or not
var first=0;

//Javascript event listener gets runs every time the user presses a key
document.onkeyup= function(event){ 
	
	var userGuess=event.key;


	//Computer picks a random number
	var computerChoice=letterString.charAt(random_number);

	//if statement checks if the user input is a valid and not a capital letter
	if (letterString.search(userGuess)!==-1) {

		if (left>1) {
			//Win condition
			if (userGuess===computerChoice) {
				wins++;
				left=9;
				document.querySelector("#wins").innerHTML=wins;
				document.querySelector("#guesses_left").innerHTML=left;
				random_number= Math.floor(Math.random() *26);
				document.querySelector("#guesses_so_far").innerHTML=guess_string;
				alert("You Won!");
				guess_string="";
				document.querySelector("#guesses_so_far").innerHTML=guess_string;
				first=0;
				
			}
			//when user guess does not match up the computer choice
			else{

				left--;
				if (first==0) 
					guess_string+=" "+userGuess;
				else
					guess_string+=" , "+userGuess;

				 
				document.querySelector("#guesses_left").innerHTML=left;


				document.querySelector("#guesses_so_far").innerHTML=guess_string;
				first=1;

				
			}
		}
		//win and lose conditions based on if the current input is the last one or not
		else if (left==1) {
			if (userGuess===computerChoice) {
				wins++;
				left=9;
				document.querySelector("#wins").innerHTML=wins;
				document.querySelector("#guesses_left").innerHTML=left;
				random_number= Math.floor(Math.random() *26);
				document.querySelector("#guesses_so_far").innerHTML=guess_string;
				alert("You Won!");
				guess_string="";
				document.querySelector("#guesses_so_far").innerHTML=guess_string;
				first=0;


			}
			else{
				losses++;
			
			document.querySelector("#losses").innerHTML=losses;
			document.querySelector("#guesses_left").innerHTML=9;
			alert("You Lost! The computer choice was "+"\""+letterString[random_number]+"\"");
			guess_string="";	
			document.querySelector("#guesses_so_far").innerHTML=guess_string;
			first=0;
			random_number= Math.floor(Math.random() *26);
			left=9;

			}
		}
			

	}

	
	else
		alert("Please insert a valid input (a letter between a to z (not capital!))"); 
		
}






